import React, { useState, useEffect } from "react";
import axios from "../Services/axiosInterceptor"; // Axios instance with interceptors
import "../css/signIn.css"; // CSS styles for login form
import Captcha from "./Captcha"; // Custom CAPTCHA component
import Cookies from "js-cookie"; // For handling cookies (token, rememberMe, etc.)
import { useNavigate } from "react-router-dom"; // For page navigation
import { auth, googleProvider } from "../firebaseConfig/FireConFig"; // Firebase auth
import {
  FacebookAuthProvider,
  signInWithPopup,
  fetchSignInMethodsForEmail,
} from "firebase/auth"; // Firebase social login utilities
import { ToastContainer, toast } from "react-toastify"; // Toast notifications
import "react-toastify/dist/ReactToastify.css";
import refreshIcon from "../images/refresh-removebg-preview.png"; // CAPTCHA refresh icon
import emailicon from '../images/tl.webp'; // Email icon
import pwd from "../images/password-76.png"; // Password icon
import audios from "../sound/success-340660.mp3"; // Success audio

// ---------------- UTILITY FUNCTIONS ----------------
// Base64 encode/decode for storing password in cookies
const encode = (str) => btoa(str);
const decode = (str) => atob(str);

// ---------------- MAIN COMPONENT ----------------
const Login = () => {
  const navigate = useNavigate(); // For redirecting pages

  // ---------------- FORM STATE ----------------
  const [input, setInput] = useState({ email: "", password: "" }); // Stores email & password
  const [passwordVisible, setPasswordVisible] = useState(false); // Toggle password visibility
  const [rememberMe, setRememberMe] = useState(false); // Remember Me checkbox state

  // ---------------- OTP STATE ----------------
  const [showOtpForm, setShowOtpForm] = useState(false); // Toggle OTP form
  const [otp, setOtp] = useState(""); // OTP input
  const [userEmail, setUserEmail] = useState(""); // Email for OTP verification
  const [timer, setTimer] = useState(180); // OTP countdown in seconds
  const [canResend, setCanResend] = useState(false); // Enable "Resend OTP" button

  // ---------------- LOCKOUT STATE ----------------
  const [lockTimer, setLockTimer] = useState(0); // Lock countdown timer
  const [failedAttempts, setFailedAttempts] = useState(0); // Track login attempts
  const LOCK_DURATION = 15 * 60; // 15 minutes lock duration

  // ---------------- DEVICE ENFORCEMENT ----------------
  const [showDevicePrompt, setShowDevicePrompt] = useState(false); // Show device login prompt
  const [pendingToken, setPendingToken] = useState(null); // Token for forced login
  const [pendingEmail, setPendingEmail] = useState(null); // Email for forced login

  // ---------------- CAPTCHA STATE ----------------
  const generateRandomCode = (length = 5) => {
    const chars = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";
    let code = "";
    for (let i = 0; i < length; i++) {
      code += chars.charAt(Math.floor(Math.random() * chars.length));
    }
    return code;
  };
  const [randomCode, setRandomCode] = useState(generateRandomCode()); // CAPTCHA code
  const [userCode, setUserCode] = useState(""); // User input for CAPTCHA

  // ---------------- OTP ANIMATION STATE ----------------
  const [otpError, setOtpError] = useState(false); // Shake animation on error
  const [otpSuccess, setOtpSuccess] = useState(false); // Glow animation on success

  // ---------------- LOAD REMEMBERED CREDENTIALS ----------------
  useEffect(() => {
    const savedData = Cookies.get("rememberData");
    if (savedData) {
      try {
        const { email, password } = JSON.parse(savedData);
        setInput({
          email: email || "",
          password: password ? decode(password) : "",
        });
        setRememberMe(true);
      } catch {
        setInput((prev) => ({ ...prev, email: savedData }));
        setRememberMe(true);
      }
    }
  }, []);

  // ---------------- PLAY SUCCESS AUDIO ----------------
  const veriFyOtpSound = () => {
    try {
      const OutSound = new Audio(audios);
      OutSound.volume = 0.8;
      OutSound.play().catch(() => {
        console.warn("⚠️ Audio playback blocked until user interacts with page.");
      });
    } catch (err) {
      console.warn("⚠️ Audio play error:", err.message);
    }
  };

  // ---------------- OTP COUNTDOWN ----------------
  useEffect(() => {
    let interval;
    if (showOtpForm && timer > 0) {
      interval = setInterval(() => setTimer((prev) => prev - 1), 1000);
    } else if (timer <= 0 && showOtpForm) {
      setCanResend(true);
      toast.error("OTP expired! Please resend.");
    }
    return () => clearInterval(interval);
  }, [showOtpForm, timer]);

  // ---------------- SAVE REMEMBER ME ----------------
  const saveRememberMe = (email, password) => {
    if (rememberMe) {
      Cookies.set(
        "rememberData",
        JSON.stringify({ email, password: encode(password) }),
        { expires: 30, secure: true, sameSite: "Strict" }
      );
    } else {
      Cookies.remove("rememberData");
    }
  };

  // ---------------- LOCKOUT COUNTDOWN ----------------
  useEffect(() => {
    let lockInterval;
    if (lockTimer > 0) {
      lockInterval = setInterval(() => setLockTimer((prev) => prev - 1), 1000);
    }
    return () => clearInterval(lockInterval);
  }, [lockTimer]);

  // ---------------- FORMAT TIMER ----------------
  const formatTime = (sec) => {
    const m = Math.floor(sec / 60).toString().padStart(2, "0");
    const s = (sec % 60).toString().padStart(2, "0");
    return `${m}:${s}`;
  };

  // ---------------- SEND OTP / LOGIN ----------------
  const sendOtp = async () => {
    if (lockTimer > 0) {
      return toast.error(`Account locked! Try again in ${formatTime(lockTimer)}`);
    }

    try {
      const response = await axios.post("/api/auth/users/login", input, { timeout: 6000 });

      if (response.data.otpSent) {
        veriFyOtpSound();
        toast.success("OTP sent to your email!");
        setShowOtpForm(true);
        setUserEmail(input.email);
        setOtp("");
        setTimer(180);
        setCanResend(false);
      } else if (response.data.token) {
        // Save login cookies
        Cookies.set("token", response.data.token, { expires: 7, secure: true, sameSite: "Strict" });
        Cookies.set("name", response.data.user?.Fname || "", { expires: 7, secure: true, sameSite: "Strict" });
        Cookies.set("email", response.data.user?.email || input.email, { expires: 7, secure: true, sameSite: "Strict" });

        saveRememberMe(input.email, input.password); 
        toast.success("Login successful!");
        setFailedAttempts(0);
        navigate("/");
      } else {
        toast.error("Unexpected response from server");
      }
    } catch (err) {
      if (err.response?.status === 401) {
        setFailedAttempts((prev) => {
          const newCount = prev + 1;
          if (newCount >= 5) {
            setLockTimer(LOCK_DURATION);
            toast.error("Too many failed attempts. Account locked for 15 minutes.");
            return 0;
          } else {
            toast.error(`Wrong credentials! Attempt ${newCount} of 5`);
          }
          return newCount;
        });
      } else {
        toast.error(err.response?.data?.message || "Login failed");
      }
    }
  };

  // ---------------- VERIFY OTP ----------------
  const handleOtpVerify = async (e) => {
    e.preventDefault();
    if (!otp) return toast.error("Please enter OTP");

    try {
      const response = await axios.post("/api/auth/verify-otp", { email: userEmail, otp }, { timeout: 10000 });

      if (response.data.forceLogout) {
        setShowDevicePrompt(true);
        setPendingToken(response.data.token);
        setPendingEmail(userEmail);
        return;
      }

      Cookies.set("token", response.data.token, { expires: 7, secure: true, sameSite: "Strict" });
      Cookies.set("name", response.data.user?.Fname || "", { expires: 7, secure: true, sameSite: "Strict" });
      Cookies.set("email", response.data.user?.email || userEmail, { expires: 7, secure: true, sameSite: "Strict" });

      saveRememberMe(userEmail, input.password);
      toast.success("OTP verified! Login successful");
      navigate("/");
    } catch (err) {
      if (err.response?.status === 401) navigate("/session-expire");
      else toast.error(err.response?.data?.message || "OTP verification failed");
    }
  };

  // ---------------- FORCE LOGIN ----------------
  const forceLogin = async () => {
    veriFyOtpSound();
    try {
      const response = await axios.post("/api/auth/users/forceLogout", { token: pendingToken, email: pendingEmail });
      if (response.data.token) {
        Cookies.set("token", response.data.token, { expires: 7, secure: true, sameSite: "Strict" });
        Cookies.set("name", response.data.user?.Fname || "", { expires: 7, secure: true, sameSite: "Strict" });
        Cookies.set("email", response.data.user?.email || pendingEmail, { expires: 7, secure: true, sameSite: "Strict" });

        saveRememberMe(input.email, input.password); 
        toast.success("Logged in successfully! Previous session ended.");
        setShowDevicePrompt(false);
        navigate("/");
      } else toast.error("Force login failed: No token returned");
    } catch (err) {
      toast.error(err.response?.data?.message || "Force login failed");
      setShowDevicePrompt(false);
    }
  };

  const cancelForceLogin = () => {
    setShowDevicePrompt(false);
    setPendingToken(null);
    setPendingEmail(null);
    setOtp("");
    toast.info("Login cancelled. Redirecting to login page...");
    navigate("/login");
  };

  // ---------------- GOOGLE LOGIN ----------------
  const googleSign = async () => {
    try {
      const result = await signInWithPopup(auth, googleProvider);
      const user = result.user;
      const response = await axios.post("/api/auth/users/google-login", { email: user.email, uid: user.uid, name: user.displayName, photoURL: user.photoURL }, { timeout: 10000 });

      if (response.data.isNewUser) {
        toast.info("Email not registered! Please register first.");
        setTimeout(() => navigate("/register"), 3000);
        return;
      }

      if (response.data.token) {
        Cookies.set("token", response.data.token, { expires: 7, secure: true, sameSite: "Strict" });
        Cookies.set("name", user.displayName, { expires: 7, secure: true, sameSite: "Strict" });
        Cookies.set("email", user.email, { expires: 7, secure: true, sameSite: "Strict" });
        saveRememberMe(input.email, input.password);
        toast.success("Login successful!");
        navigate("/");
      }
    } catch (err) {
      if (err.code === "auth/account-exists-with-different-credential") {
        const methods = await fetchSignInMethodsForEmail(auth, err.customData.email);
        toast.error(`An account exists with ${methods[0]}. Use that to login.`);
      } else if (err.code === "auth/popup-closed-by-user") toast.info("Login cancelled.");
      else toast.error(err.message || "Google login failed");
    }
  };

  // ---------------- FACEBOOK LOGIN ----------------
  const facebookSign = async () => {
    const facebookProvider = new FacebookAuthProvider();
    try {
      const result = await signInWithPopup(auth, facebookProvider);
      const user = result.user;
      const response = await axios.post("/api/auth/users/facebook-login", { email: user.email, uid: user.uid, name: user.displayName, photoURL: user.photoURL }, { timeout: 10000 });

      if (response.data.forceLogout) {
        setShowDevicePrompt(true);
        setPendingToken(response.data.token);
        setPendingEmail(user.email);
        return;
      }

      if (response.data.token) {
        Cookies.set("token", response.data.token, { expires: 7, secure: true, sameSite: "Strict" });
        Cookies.set("name", user.displayName, { expires: 7, secure: true, sameSite: "Strict" });
        Cookies.set("email", user.email, { expires: 7, secure: true, sameSite: "Strict" });
        saveRememberMe(input.email, input.password);
        toast.success("Facebook login successful!");
        navigate("/");
      }
    } catch (error) {
      if (error.code === "auth/account-exists-with-different-credential") {
        const methods = await fetchSignInMethodsForEmail(auth, error.customData.email);
        toast.error(`An account exists with ${methods[0]}. Use that to login.`);
      } else if (error.code === "auth/popup-closed-by-user") toast.info("Login cancelled.");
      else toast.error(error.message || "Facebook login failed");
    }
  };

  // ---------------- RENDER UI ----------------
  // Show lock screen if account locked
  if (lockTimer > 0) {
    return (
      <div className="bg-white p-6 rounded-lg shadow-md w-96 singform text-center">
        <ToastContainer position="top-right" autoClose={3000} />
        <h2 className="text-2xl font-bold mb-4 text-center">Account Locked</h2>
        <p className="mb-4 text-red-500">Too many failed login attempts.</p>
        <p className="text-gray-700">Please try again after:</p>
        <p className="text-xl font-bold mt-2">{formatTime(lockTimer)}</p>
        <button className="mt-4 px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600" onClick={() => navigate("/register")}>Sign Up</button>
      </div>
    );
  }

  // Show device login prompt if forced logout detected
  if (showDevicePrompt) {
    return (
      <div className="bg-white p-6 rounded-lg shadow-md w-96 singform text-center">
        <ToastContainer position="top-right" autoClose={3000} />
        <h2 className="text-xl font-bold mb-4">⚠️ You're currently logged in on another device</h2>
        <p className="mb-4">Logging in here will sign you out from that session.</p>
        <div className="flex justify-around mt-4">
          <button onClick={forceLogin} className="px-4 py-2 bg-green-500 text-white rounded hover:bg-green-600">Proceed to Login</button>
          <button onClick={cancelForceLogin} className="px-4 py-2 bg-gray-400 text-white rounded hover:bg-gray-500">Cancel</button>
        </div>
      </div>
    );
  }

  // Normal login form
  return (
    <div className="bg-white p-3 rounded-lg shadow-md w-96 singform gray-500 mag">
      <ToastContainer position="top-right" autoClose={3000} />
      <h2 className="text-2xl font-bold mb-4 text-center">Login</h2>
      {/* Render login form or OTP form */}
      {!showOtpForm ? (
        // Email & Password form + CAPTCHA + Social login
        <form className="space-y-3">
          {/* ... JSX as in original code ... */}
        </form>
      ) : (
        // OTP verification form
        <form onSubmit={handleOtpVerify} className="space-y-3">
          {/* ... JSX as in original code ... */}
        </form>
      )}
    </div>
  );
};

export default Login;
